import jsonimport osclass Config:    """    Load config of import, performance table    """    config = None    @staticmethod    def load_config(reload=True):        """        Load config json file in to memory        @param reload: If false, do not process when config already loaded        @return: Nothing        """        if Config.config is not None and not reload:            return        location = os.path.realpath(            os.path.join(os.getcwd(), os.path.dirname(__file__)))        with open(os.path.join(location, 'config.json'), 'r',                  encoding='utf-8') as f:            Config.config = json.load(f)    @staticmethod    def get_int_value(entry, key, default=0):        """        Get data and convert to int from config info        @param entry: dict        @param key:        @param default:        @return:        """        value = Config.get_value(entry, key, None)        if value is None:            return default        return int(value)    @staticmethod    def get_enum_value(entry, key, enum_class, default=None):        """        Get data and convert to Enum from config info        @type entry: dict        @type key: str        @type enum_class: Enum        @param entry:        @param key:        @param enum_class: Enum class        @param default:        @return:        """        value = Config.get_value(entry, key, default)        if value is None:            return default        return enum_class(value)    @staticmethod    def get_value(entry, key, default=None):        """        Get value with specified key in dict entry        @type entry: dict        @param entry:        @param key:        @param default:        @return:        """        if key not in entry:            return default        return entry[key]    @staticmethod    def none_to_default(value, default=0):        """        Convert none        @param value:        @param default:        @return:        """        if value is None:            return default        return value